import React, {useContext, useState, useEffect} from 'react';
import "./Messenger.css"
import Topbar from '../../components/Topbar/Topbar';
import Conversations from '../../components/conversations/Conversations';
import Message from '../../components/message/Message';
import ChatOnline from '../../components/ChatOnline/ChatOnline';
import {AuthContext} from "../../context/AuthContext";
import { LineAxisOutlined } from '@mui/icons-material';
import axios from "axios";


function Messenger() {
  const [conversation , setConversation]= useState([]);
  const[ currChat , setCurrChat]= useState(null);
  const [ message , setMessage]= useState([]);
  const {user}= useContext(AuthContext);
  
  useEffect(() => {
    const getConversations= async()=>{
      try{
      const res= await axios.get(`http://localhost:4000/api/getconversation/${user._id}`);
      console.log("conversation", res);
      setConversation(res.data);
      }catch(err){
        console.log(err);
      }
    }
    getConversations();
  }, [user._id]);
  

  return (
    <>
    <Topbar/>
    <div className='messenger'>
        <div className="chatMenu">
            <div className="chatMenuWrapper">
                <input placeholder='search for friends' className='chatMenuInput' />
                {conversation.map((c)=>(
                  <Conversations convo={c} currUser={user} />
                ))}
            </div>
        </div>
        <div className="chatBox">
            <div className="chatBoxWrapper">
              {currChat ? 
              (<>
              <div className="chatBoxTop">
                <Message/>
                <Message own={true}/>
                <Message/>
                <Message own={true} />
              </div>
              <div className="chatBoxBottom">
                <textarea className='chatMessageInput' placeholder='write your message..'>
                </textarea>
                <button className='chatSubmitButton'>Send</button>
              </div>
              </> )
              : (<span className=''>Open a conversation to start a chat</span>
              )}
              </div>
            
        </div>
        <div className="chatOnline">
            <div className="chatOnlineWrapper">
               <ChatOnline/>
            </div>
        </div>
    </div>
    </>
  )
}

export default Messenger
