import React from 'react'
import "./post.css";
import {MoreVert, heart , ThumbUpSharp} from "@mui/icons-material";

function Post() {
//   const [like,setLike]=useState(1);
//   const [isLiked , setIsLiked]=useState(false);

//   const likeHandler=()=>{
//     setLike(isLiked ? like-1 : like+1);
//     setIsLiked(!isLiked);
//   }

  const PF= process.env.REACT_APP_PUBLIC_FOLDER;

  //TODO : ADD PUBLIC THING IN POST !

  return (
    <div className="post">
        <div className="postWrapper">
            <div className="postTop">
                <div className="postTopLeft">
                    <img src="/assets/profile.jfif" alt="" className="postProfileImg" />
                    <span className="postUsername">Sejal Rai</span>
                    <span className="postDate">2 mins ago</span>
                </div>
                <div className="postTopRight">
                    <MoreVert/>
                </div>
            </div>
            <div className="postCenter">
                <span className="postText">Hey! My first Postt yeahhh</span>
                <img src="/assets/profile.jfif" alt="" className="postImg" />

            </div>
            <div className="postBottom">
                <div className="postBottomLeft">
                    <img src={`${PF}like.png`} alt="" className="likeIcon" />
                    <img src={`${PF}heart.png`} alt="" className="likeIcon" />
                    <span className="postLikeCounter">32 people liked it</span>
                </div>
                <div className="postBottomRight">
                    <span className="postCommentText">9 comments</span>
                </div>
            </div>

        </div>
    </div>
  )
}

export default Post
